#include<iostream>
#include<vector>
#include<String>

using namespace std;
string int2str(int num);
int main()
{
        string S="";
        string res;
        int count=1;
        for(int i=1;i<S.size();i++)
        {
            if(S[i]==S[i-1]) count++;
            else {
                res+=S[i-1];
                if(count<10) res+='0'+count;
                else res+=int2str(count);
                count=1;
            }
        }
        res+=S[S.size()-1];
        if(count<10) res+='0'+count;
        else res+=int2str(count);
}

string int2str(int num)
    {
        string str;
        for(int i=10000;i!=0;i/=10)
        {
            int tmp=num%(i*10)/i;
            if(tmp==0&&i!=1) continue;
            else str+='0'+tmp;
        }
        return str;
    }